import React, { useState, useEffect } from 'react';
import { BlocklyWorkspace } from 'react-blockly';
import * as Blockly from 'blockly/core';
import 'blockly/blocks';
import { pythonGenerator } from 'blockly/python';
import Editor from 'react-simple-code-editor';
import { highlight, languages } from 'prismjs';
import 'prismjs/components/prism-python';
import 'prismjs/themes/prism-tomorrow.css';
import './App.css';
import './blocks/time_blocks';

// Định nghĩa màu sắc cho các khối
Blockly.HSV_SATURATION = 0.45;
Blockly.HSV_VALUE = 0.65;

const INITIAL_TOOLBOX_JSON = {
  "kind": "categoryToolbox",
  "contents": [
    {
      "kind": "category",
      "name": "Logic",
      "categorystyle": "logic_category",
      "contents": [
        { "kind": "block", "type": "controls_if" },
        { "kind": "block", "type": "logic_compare" },
        { "kind": "block", "type": "logic_operation" },
        { "kind": "block", "type": "logic_negate" }
      ]
    },
    {
      "kind": "category",
      "name": "Loops",
      "categorystyle": "loop_category",
      "contents": [
        { "kind": "block", "type": "controls_repeat_ext" },
        { "kind": "block", "type": "controls_whileUntil" },
        { "kind": "block", "type": "controls_for" }
      ]
    },
    {
      "kind": "category",
      "name": "Math",
      "categorystyle": "math_category",
      "contents": [
        { "kind": "block", "type": "math_number" },
        { "kind": "block", "type": "math_arithmetic" },
        { "kind": "block", "type": "math_single" }
      ]
    },
    {
      "kind": "category",
      "name": "Text",
      "categorystyle": "text_category",
      "contents": [
        { "kind": "block", "type": "text" },
        { "kind": "block", "type": "text_print" },
        { "kind": "block", "type": "text_join" }
      ]
    },
    {
      "kind": "category",
      "name": "Lists",
      "categorystyle": "list_category",
      "contents": [
        { "kind": "block", "type": "lists_create_empty" },
        { "kind": "block", "type": "lists_create_with" },
        { "kind": "block", "type": "lists_repeat" },
        { "kind": "block", "type": "lists_length" },
        { "kind": "block", "type": "lists_getIndex" },
        { "kind": "block", "type": "lists_setIndex" }
      ]
    },
    {
      "kind": "category",
      "name": "Time",
      "categorystyle": "time_category",
      "contents": [
        { "kind": "block", "type": "time_sleep" },
        { "kind": "block", "type": "time_time" }
      ]
    },
    {
      "kind": "category",
      "name": "Variables",
      "categorystyle": "variable_category",
      "custom": "VARIABLE"
    },
    {
      "kind": "category", 
      "name": "Functions",
      "categorystyle": "procedure_category",
      "custom": "PROCEDURE"
    }
  ]
};

// Định nghĩa theme cho Blockly
const blocklyTheme = Blockly.Theme.defineTheme('custom', {
  'base': Blockly.Themes.Classic,
  'componentStyles': {
    'workspaceBackgroundColour': '#ffffff',
    'toolboxBackgroundColour': '#f0f0f0',
    'toolboxForegroundColour': '#333',
    'flyoutBackgroundColour': '#eee',
    'flyoutForegroundColour': '#444',
    'scrollbarColour': '#ccc',
    'insertionMarkerColour': '#fff',
    'insertionMarkerOpacity': 0.3,
    'scrollbarOpacity': 0.4,
    'cursorColour': '#d0d0d0'
  },
  'categoryStyles': {
    'logic_category': { 'colour': '210' },
    'loop_category': { 'colour': '120' },
    'math_category': { 'colour': '230' },
    'text_category': { 'colour': '160' },
    'list_category': { 'colour': '260' },
    'time_category': { 'colour': '290' },
    'variable_category': { 'colour': '330' },
    'procedure_category': { 'colour': '290' }
  }
});

function App() {
  const [pythonCode, setPythonCode] = useState('');
  const [blocklyWorkspace, setBlocklyWorkspace] = useState(null);
  const [output, setOutput] = useState('');
  const [isRunning, setIsRunning] = useState(false);
  const [useTerminal, setUseTerminal] = useState(false);
  const [outputLines, setOutputLines] = useState([]);

  useEffect(() => {
    if (blocklyWorkspace) {
      const updateCode = () => {
        try {
          const code = pythonGenerator.workspaceToCode(blocklyWorkspace);
          setPythonCode(code);
        } catch (error) {
          console.error('Error generating code:', error);
          setOutput('Error generating code: ' + error.message);
        }
      };

      blocklyWorkspace.addChangeListener(updateCode);
      return () => {
        blocklyWorkspace.removeChangeListener(updateCode);
      };
    }
  }, [blocklyWorkspace]);

  const handleWorkspaceChange = (workspace) => {
    setBlocklyWorkspace(workspace);
  };

  // Function to poll for new output
  const pollOutput = async () => {
    try {
      const response = await fetch('http://localhost:5000/get_output');
      const data = await response.json();
      if (data.outputs && data.outputs.length > 0) {
        setOutputLines(prev => [...prev, ...data.outputs]);
      }
    } catch (error) {
      console.error('Error polling output:', error);
    }
  };

  // Start polling when code is running
  useEffect(() => {
    let pollInterval;
    if (isRunning) {
      pollInterval = setInterval(pollOutput, 100);
    }
    return () => {
      if (pollInterval) {
        clearInterval(pollInterval);
      }
    };
  }, [isRunning]);

  const handleRunCode = async () => {
    try {
      setIsRunning(true);
      setOutputLines([]);
      
      const response = await fetch('http://localhost:5000/run', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          code: pythonCode,
          use_terminal: useTerminal
        }),
      });
      
      const data = await response.json();
      
      if (data.error) {
        setOutput(`Error: ${data.error}`);
      } else if (useTerminal) {
        setOutput('Code is running in a new terminal window');
      }
    } catch (error) {
      setOutput(`Error: ${error.message}`);
    } finally {
      setIsRunning(false);
    }
  };

  const handleSaveBlocks = async () => {
    try {
      if (blocklyWorkspace) {
        const xml = Blockly.serialization.workspaces.save(blocklyWorkspace);
        const xmlStr = JSON.stringify(xml, null, 2);
        
        const response = await fetch('http://localhost:5000/save', {
          method: 'POST',
          headers: {
            'Content-Type': 'application/json',
          },
          body: JSON.stringify({ content: xmlStr }),
        });
        
        const result = await response.json();
        if (result.error) {
          setOutput('Error saving blocks: ' + result.error);
        } else {
          setOutput('Blocks saved successfully to: ' + result.path);
        }
      }
    } catch (error) {
      console.error('Error saving blocks:', error);
      setOutput('Error saving blocks: ' + error.message);
    }
  };

  const handleLoadBlocks = (event) => {
    const file = event.target.files[0];
    if (file && blocklyWorkspace) {
      const reader = new FileReader();
      reader.onload = (e) => {
        try {
          const json = JSON.parse(e.target.result);
          Blockly.serialization.workspaces.load(json, blocklyWorkspace);
        } catch (error) {
          console.error('Error loading blocks:', error);
          setOutput('Error loading blocks: ' + error.message);
        }
      };
      reader.readAsText(file);
    }
  };

  return (
    <div className="app-container">
      <div className="toolbar">
        <button onClick={handleRunCode}>Run Code</button>
        <button onClick={handleSaveBlocks}>Save Blocks</button>
        <input
          type="file"
          accept=".json"
          onChange={handleLoadBlocks}
          style={{ display: 'none' }}
          id="load-blocks"
        />
        <label htmlFor="load-blocks" className="button">
          Load Blocks
        </label>
      </div>
      <div className="main-content">
        <div className="blockly-container">
          <BlocklyWorkspace
            toolboxConfiguration={INITIAL_TOOLBOX_JSON}
            initialXml='<xml xmlns="https://developers.google.com/blockly/xml"></xml>'
            className="blockly-editor"
            onWorkspaceChange={handleWorkspaceChange}
            workspaceConfiguration={{
              grid: {
                spacing: 20,
                length: 3,
                colour: '#ccc',
                snap: true,
              },
              zoom: {
                controls: true,
                wheel: true,
                startScale: 1.0,
                maxScale: 2,
                minScale: 0.4,
                scaleSpeed: 1.1
              },
              trashcan: true,
              renderer: 'geras',
              move: {
                drag: true,
                wheel: true
              },
              theme: blocklyTheme,
              media: '/lib/blockly/media/'
            }}
          />
        </div>
        <div className="right-panel">
          <div className="code-container">
            <div className="code-header">
              <h2>Generated Python Code</h2>
              <div className="run-controls">
                <label className="terminal-toggle">
                  <input
                    type="checkbox"
                    checked={useTerminal}
                    onChange={(e) => setUseTerminal(e.target.checked)}
                  />
                  Run in Terminal
                </label>
                <button 
                  onClick={handleRunCode} 
                  disabled={isRunning}
                  className="run-button"
                >
                  {isRunning ? 'Running...' : 'Run Code'}
                </button>
              </div>
            </div>
            <div className="editor-container">
              <Editor
                value={pythonCode || '# Generated Python code will appear here'}
                onValueChange={code => setPythonCode(code)}
                highlight={code => highlight(code, languages.python)}
                padding={10}
                textareaId="code-editor"
                textareaClassName="code-editor"
                preClassName="code-output"
                style={{
                  fontFamily: '"Fira code", "Fira Mono", monospace',
                  fontSize: 14,
                  backgroundColor: '#1e1e1e',
                  color: '#d4d4d4',
                  borderRadius: '4px',
                  height: '100%',
                  overflow: 'auto'
                }}
              />
            </div>
            <div className="output-container">
              <h2>Output</h2>
              <div className="output-content">
                {outputLines.map((line, index) => (
                  <div key={index} className="output-line">{line}</div>
                ))}
                {output && <div className="output-line">{output}</div>}
              </div>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}

export default App;
