import Blockly from 'blockly/core';
import 'blockly/blocks';
import { pythonGenerator } from 'blockly/python';

Blockly.Blocks['struct_create'] = {
  init: function() {
    this.appendValueInput('NAME')
        .setCheck('String')
        .appendField('create struct named');
    this.appendStatementInput('FIELDS')
        .setCheck(null)
        .appendField('with fields');
    this.setPreviousStatement(true, null);
    this.setNextStatement(true, null);
    this.setColour(160);
    this.setTooltip('Create a struct with the given name and fields');
  }
};

pythonGenerator.forBlock['struct_create'] = function(block) {
  const name = pythonGenerator.valueToCode(block, 'NAME', pythonGenerator.ORDER_ATOMIC) || 'MyStruct';
  const fields = pythonGenerator.statementToCode(block, 'FIELDS');
  return `class ${name}:\n  def __init__(self):\n${fields}`;
};
