import Blockly from 'blockly/core';
import 'blockly/blocks';
import { pythonGenerator } from 'blockly/python';

Blockly.Blocks['arrays_create'] = {
  init: function() {
    this.appendValueInput('VALUE')
        .setCheck(null)
        .appendField('create array with');
    this.setPreviousStatement(true, null);
    this.setNextStatement(true, null);
    this.setColour(160);
    this.setTooltip('Create an array with the given value');
  }
};

pythonGenerator.forBlock['arrays_create'] = function(block) {
  const value = pythonGenerator.valueToCode(block, 'VALUE', pythonGenerator.ORDER_ATOMIC) || '[]';
  return `array = ${value}\n`;
};
