import Blockly from 'blockly/core';
import 'blockly/blocks';
import { pythonGenerator } from 'blockly/python';

Blockly.Blocks['class_create'] = {
  init: function() {
    this.appendValueInput('NAME')
        .setCheck('String')
        .appendField('create class named');
    this.appendStatementInput('METHODS')
        .setCheck(null)
        .appendField('with methods');
    this.setPreviousStatement(true, null);
    this.setNextStatement(true, null);
    this.setColour(160);
    this.setTooltip('Create a class with the given name and methods');
  }
};

pythonGenerator.forBlock['class_create'] = function(block) {
  const name = pythonGenerator.valueToCode(block, 'NAME', pythonGenerator.ORDER_ATOMIC) || 'MyClass';
  const methods = pythonGenerator.statementToCode(block, 'METHODS');
  return `class ${name}:\n${methods}`;
};
