import React, { useState, useEffect, useRef } from 'react';
import { BlocklyWorkspace } from 'react-blockly';
import Blockly from 'blockly';
import 'blockly/python';
import Editor from '@monaco-editor/react';
import './App.css';

const INITIAL_TOOLBOX_JSON = {
  "kind": "categoryToolbox",
  "contents": [
    {
      "kind": "category",
      "name": "Logic",
      "colour": "%{BKY_LOGIC_HUE}",
      "contents": [
        { "kind": "block", "type": "controls_if" },
        { "kind": "block", "type": "logic_compare" },
        { "kind": "block", "type": "logic_operation" },
        { "kind": "block", "type": "logic_negate" }
      ]
    },
    {
      "kind": "category",
      "name": "Loops",
      "colour": "%{BKY_LOOPS_HUE}",
      "contents": [
        { "kind": "block", "type": "controls_repeat_ext" },
        { "kind": "block", "type": "controls_whileUntil" },
        { "kind": "block", "type": "controls_for" }
      ]
    },
    {
      "kind": "category",
      "name": "Math",
      "colour": "%{BKY_MATH_HUE}",
      "contents": [
        { "kind": "block", "type": "math_number" },
        { "kind": "block", "type": "math_arithmetic" },
        { "kind": "block", "type": "math_single" }
      ]
    },
    {
      "kind": "category",
      "name": "Text",
      "colour": "%{BKY_TEXTS_HUE}",
      "contents": [
        { "kind": "block", "type": "text" },
        { "kind": "block", "type": "text_print" },
        { "kind": "block", "type": "text_join" }
      ]
    },
    {
      "kind": "category",
      "name": "Lists",
      "colour": "%{BKY_LISTS_HUE}",
      "contents": [
        { "kind": "block", "type": "lists_create_empty" },
        { "kind": "block", "type": "lists_create_with" },
        { "kind": "block", "type": "lists_repeat" },
        { "kind": "block", "type": "lists_length" },
        { "kind": "block", "type": "lists_getIndex" },
        { "kind": "block", "type": "lists_setIndex" }
      ]
    },
    {
      "kind": "category",
      "name": "Variables",
      "custom": "VARIABLE",
      "colour": "%{BKY_VARIABLES_HUE}"
    },
    {
      "kind": "category",
      "name": "Functions",
      "custom": "PROCEDURE",
      "colour": "%{BKY_PROCEDURES_HUE}"
    }
  ]
};

function App() {
  const [pythonCode, setPythonCode] = useState('');
  const workspaceRef = useRef(null);

  const handleWorkspaceChange = (workspace) => {
    workspaceRef.current = workspace;
    const code = Blockly.Python.workspaceToCode(workspace);
    setPythonCode(code);
  };

  // Initialize workspace with some default blocks
  const initialXml = '<xml xmlns="https://developers.google.com/blockly/xml"></xml>';

  return (
    <div className="app-container">
      <div className="blockly-container">
        <BlocklyWorkspace
          toolboxConfiguration={INITIAL_TOOLBOX_JSON}
          initialXml={initialXml}
          className="blockly-editor"
          onWorkspaceChange={handleWorkspaceChange}
          onXmlChange={() => {}}
          workspaceConfiguration={{
            grid: {
              spacing: 20,
              length: 3,
              colour: '#ccc',
              snap: true,
            },
            zoom: {
              controls: true,
              wheel: true,
              startScale: 1.0,
              maxScale: 3,
              minScale: 0.3,
              scaleSpeed: 1.2
            },
            trashcan: true
          }}
        />
      </div>
      <div className="code-container">
        <Editor
          height="100%"
          defaultLanguage="python"
          value={pythonCode}
          theme="vs-dark"
          options={{
            readOnly: true,
            minimap: { enabled: true },
            fontSize: 14,
            automaticLayout: true
          }}
        />
      </div>
    </div>
  );
}

export default App;
